<?php

namespace Tests\Unit\ActivityPub;

use App\Util\ActivityPub\Helpers;
use Tests\TestCase;

class RemoteFollowTest extends TestCase
{
    protected string $mastodon;

    public function setUp(): void
    {
        parent::setUp();

        $this->mastodon = '{"type":"Follow","signature":{"type":"RsaSignature2017","signatureValue":"","creator":"http://mastodon.example.org/users/admin#main-key","created":"2018-02-17T13:29:31Z"},"object":"http://localtesting.pleroma.lol/users/lain","nickname":"lain","id":"http://mastodon.example.org/users/admin#follows/2","actor":"http://mastodon.example.org/users/admin","@context":["https://www.w3.org/ns/activitystreams","https://w3id.org/security/v1",{"toot":"http://joinmastodon.org/ns#","sensitive":"as:sensitive","ostatus":"http://ostatus.org#","movedTo":"as:movedTo","manuallyApprovesFollowers":"as:manuallyApprovesFollowers","inReplyToAtomUri":"ostatus:inReplyToAtomUri","conversation":"ostatus:conversation","atomUri":"ostatus:atomUri","Hashtag":"as:Hashtag","Emoji":"toot:Emoji"}]}';

    }

    /** @test */
    public function validateMastodonFollowObject()
    {
        $mastodon = json_decode($this->mastodon, true);
        $mastodon = Helpers::validateObject($mastodon);
        $this->assertTrue($mastodon);
    }
}
